import jsPDF from "jspdf";
import "jspdf-autotable";
import autoTable from "jspdf-autotable";
import * as XLSX from "xlsx";

export default function ReportButtons({ datos = [], nombre = "Reporte" }) {
  // ---- LOGO (BASE64 PNG) ----
  const logoMP = "data:image/png;base64,";

  // Detectar columnas automticamente
  const columnas = datos.length > 0 ? Object.keys(datos[0]) : [];

  // -------------------------------
  //   GENERAR PDF
  // -------------------------------
  const exportarPDF = () => {
    const doc = new jsPDF("landscape");
    const pageWidth = doc.internal.pageSize.getWidth();

    const rows = datos.map((item) =>
      columnas.map((col) => {
        let value = item[col];

        if (value && value.toString().includes("T")) {
          const d = new Date(value);
          value = d.toLocaleString("es-GT");
        }

        return value ?? "";
      })
    );

    autoTable(doc, {
      head: [columnas],
      body: rows,
      startY: 50,
      theme: "grid",

      styles: {
        fontSize: 8,
        cellWidth: "wrap",
      },

      headStyles: {
        fillColor: [30, 64, 175],
        textColor: 255,
        fontSize: 9,
        halign: "center",
      },

      alternateRowStyles: {
        fillColor: [245, 245, 245],
      },

      margin: { top: 50, left: 10, right: 10 },

      didDrawPage: () => {
        // LOGO
        doc.addImage(logoMP, "PNG", 10, 10, 32, 32);

        // TTULO
        doc.setFontSize(16);
        doc.setTextColor(40);
        doc.text(nombre, pageWidth / 2, 22, {
          align: "center",
        });

        // LNEA DECORATIVA
        doc.setDrawColor(150);
        doc.setLineWidth(0.5);
        doc.line(10, 45, pageWidth - 10, 45);

        // PIE DE PGINA
        const pageNum = doc.internal.getNumberOfPages();
        doc.setFontSize(10);
        doc.text(`Pgina ${pageNum}`, pageWidth - 20, doc.internal.pageSize.getHeight() - 10);
      },
    });

    doc.save(`${nombre}.pdf`);
  };

  // -------------------------------
  //   GENERAR EXCEL
  // -------------------------------
  const exportarExcel = () => {
    const hoja = XLSX.utils.json_to_sheet(datos);
    const libro = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(libro, hoja, "Datos");
    XLSX.writeFile(libro, `${nombre}.xlsx`);
  };

  // -------------------------------
  //   GENERAR CSV
  // -------------------------------
  const exportarCSV = () => {
    const csvRows = [];
    csvRows.push(columnas.join(","));

    datos.forEach((item) => {
      const values = columnas.map((col) => `"${item[col] ?? ""}"`);
      csvRows.push(values.join(","));
    });

    const blob = new Blob([csvRows.join("\n")], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);

    const a = document.createElement("a");
    a.href = url;
    a.download = `${nombre}.csv`;
    a.click();
  };

  // -------------------------------
  //   COPIAR AL PORTAPAPELES
  // -------------------------------
  const copiar = () => {
    let texto = columnas.join("\t") + "\n";

    datos.forEach((item) => {
      texto += columnas.map((c) => item[c] ?? "").join("\t") + "\n";
    });

    navigator.clipboard.writeText(texto);
    alert("Datos copiados al portapapeles");
  };

  return (
    <div className="flex gap-3 mb-4">
      <button onClick={exportarPDF} className="px-3 py-1 bg-gray-300 hover:bg-gray-400 rounded">
        PDF
      </button>

      <button onClick={exportarExcel} className="px-3 py-1 bg-gray-300 hover:bg-gray-400 rounded">
        Excel
      </button>

      <button onClick={exportarCSV} className="px-3 py-1 bg-gray-300 hover:bg-gray-400 rounded">
        CSV
      </button>

      <button onClick={copiar} className="px-3 py-1 bg-gray-300 hover:bg-gray-400 rounded">
        Copiar
      </button>
    </div>
  );
}
